function responsiveSvgSizer() {
    const svg = d3.select('#graphSvg');
    const rect = svg.node().parentNode.getBoundingClientRect();
    const dpr = window.devicePixelRatio;

    svg.attr("width", rect.width * dpr)
        .attr("height", 600 * dpr)
        .style("width", `${rect.width}px`)
        .style("height", `${600}px`);
	
}